import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';
export default function Signup(){ const [name,setName]=useState(''); const [email,setEmail]=useState(''); const [password,setPassword]=useState(''); const [course,setCourse]=useState('MERN Bootcamp'); const [err,setErr]=useState(''); const navigate=useNavigate();
  const submit=async(e)=>{ e.preventDefault(); try{ const res=await api.post('/auth/register',{name,email,password,role:'student',course}); localStorage.setItem('token',res.token); navigate('/student'); }catch(error){ setErr(error.msg||'Signup failed'); } };
  return (<div style={{maxWidth:520,margin:'40px auto'}}><h2>Sign up (Student)</h2><form onSubmit={submit}><input placeholder='Name' value={name} onChange={e=>setName(e.target.value)} required/><br/><input placeholder='Email' value={email} onChange={e=>setEmail(e.target.value)} required/><br/><input placeholder='Password' type='password' value={password} onChange={e=>setPassword(e.target.value)} required/><br/><input placeholder='Course' value={course} onChange={e=>setCourse(e.target.value)} required/><br/><button type='submit'>Create account</button></form><div style={{color:'red'}}>{err}</div><p>Already have account? <a href='/login'>Login</a></p></div>);
}
