import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';
export default function Login(){ const [email,setEmail]=useState(''); const [password,setPassword]=useState(''); const [err,setErr]=useState(''); const navigate=useNavigate();
  const submit=async(e)=>{ e.preventDefault(); try{ const res=await api.post('/auth/login',{email,password}); localStorage.setItem('token',res.token); const me=await api.get('/auth/me'); if(me.user.role==='admin') navigate('/admin'); else navigate('/student'); }catch(error){ setErr(error.msg||'Login failed'); } };
  return (<div style={{maxWidth:420,margin:'40px auto'}}><h2>Login</h2><form onSubmit={submit}><input placeholder='Email' value={email} onChange={e=>setEmail(e.target.value)} required/><br/><input placeholder='Password' type='password' value={password} onChange={e=>setPassword(e.target.value)} required/><br/><button type='submit'>Login</button></form><div style={{color:'red'}}>{err}</div><p>Don't have an account? <a href='/signup'>Sign up</a></p></div>);
}
