import React, { useEffect, useState } from 'react';
import api from '../services/api';
export default function StudentDashboard(){ const [student,setStudent]=useState(null); const [form,setForm]=useState({name:'',email:'',course:''}); const [msg,setMsg]=useState('');
  useEffect(()=>{(async()=>{ try{ const s = await api.get('/students/me'); setStudent(s); setForm({name:s.name,email:s.email,course:s.course}); }catch(err){console.error(err);} })();},[]);
  const submit=async(e)=>{ e.preventDefault(); try{ await api.put(`/students/${student.id}`,form); setMsg('Updated'); }catch(err){ setMsg(err.msg||'Error'); } };
  if(!student) return <div>Loading...</div>;
  return (<div style={{maxWidth:600,margin:'20px auto'}}><h2>Student Dashboard</h2><form onSubmit={submit}><input placeholder='Name' value={form.name} onChange={e=>setForm({...form,name:e.target.value})} required/><br/><input placeholder='Email' value={form.email} onChange={e=>setForm({...form,email:e.target.value})} required/><br/><input placeholder='Course' value={form.course} onChange={e=>setForm({...form,course:e.target.value})} required/><br/><button type='submit'>Save</button></form><div>{msg}</div><p>Enrollment: {new Date(student.enrollmentDate).toLocaleDateString()}</p><p><a href='/login' onClick={()=>{localStorage.removeItem('token');}}>Logout</a></p></div>); }
